    })
  }

render(){
  const {robots, searchfield} = this.state;//destructuring
  const filteredBots = robots.filter((bot) => {
    return bot.name.toLowerCase().includes(searchfield.toLowerCase());
  });

  if(robots.length){
    return (
      <div className = 'App'>
        <h1>RoboFriends</h1>
        <SearchBox onSearchChange = {this.onSearchChange}/>
        <Scroll>
          <ErrorBoundary>
            <CardList bots = {filteredBots}/>
          </ErrorBoundary>
        </Scroll>
      </div>
    );
  } else{
    return 'LOADING...PLEASE WAIT'
  }
}
*/

  render(){
    const {robots} = this.state;//destructuring
    const {searchField, onSearchChange} = this.props;//added from redux
    const filteredBots = robots.filter((bot) => {
      return bot.name.toLowerCase().includes(searchField.toLowerCase());
    });

    if(robots.length){
      return (
        <div className = 'App'>
          <h1>RoboFriends</h1>
          <SearchBox onSearchChange = {onSearchChange}/>
          <Scroll>
            <ErrorBoundary>
              <CardList bots = {filteredBots}/>
            </ErrorBoundary>
          </Scroll>
        </div>
      );
    } else{
      return 'LOADING...PLEASE WAIT'
    }
  }

}

export default connector(App);
